                <Button 
                    variant="default" 
                    className="bg-indigo-600 text-white hover:bg-indigo-700 rounded-full w-full md:w-48 px-6"
                >
                    Explore All Products
                    <ArrowRight className="ml-2 h-4 w-4" />
                </Button>
