import FeatureTwo from "@/registry/default/blocks/feature-02/components/feature-two";

export default function Page() {
  return <FeatureTwo />;
}

"use client";

import React from "react";
import Link from "next/link";
import { motion } from "framer-motion";
import { VideoDialog } from "@/registry/default/blocks/feature-02/components/video-dialog";

interface Feature {
  title: string;
  description: string;
}

const features: Feature[] = [
  {
    title: "Pre-designed Blocks",
    description:
      "Our library includes 30+ pre-designed components such as headers, footers, login forms, CTAs, and more, to accelerate your development workflow.",
  },
  {
    title: "Customizable Variants",
    description:
      "Easily customize components with variants and Tailwind CSS to match your projectâ€™s unique design requirements.",
  },
  {
    title: "Built on ShadCN UI",
    description:
      "Leverage the power of ShadCN UI, ensuring your components are modern, responsive, and highly performant.",
  },
];

const FeatureTwo = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.5,
        ease: "easeOut",
      },
    },
  };

  const buttonVariants = {
    rest: { scale: 1 },
    hover: {
      scale: 1.05,
      transition: {
        duration: 0.2,
        ease: "easeInOut",
      },
    },
  };

  return (
    <section className="py-10 lg:py-16 bg-gradient-to-br from-black to-gray-900 rounded-2xl overflow-hidden relative">
      <motion.div
        initial="hidden"
        animate="visible"
        variants={containerVariants}
        className="px-6 mx-auto sm:px-8 lg:px-12 max-w-7xl"
      >
        <div className="grid grid-cols-1 lg:grid-cols-2 lg:gap-x-24 gap-y-16 xl:gap-x-36">
          <motion.div variants={itemVariants}>
            <h2 className="tracking-tighter text-white">
              <span className="font-sans text-4xl md:text-6xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-blue-400 to-purple-600">
                Elevate your UI
              </span>
              <span className="font-serif text-5xl italic lg:block md:text-7xl mt-2">
                design process
              </span>
              <span className="font-sans text-4xl lg:block md:text-6xl mt-2">
                with our versatile component library.
              </span>
            </h2>
            <motion.p
              variants={itemVariants}
              className="mt-8 font-sans text-lg font-normal leading-8 text-gray-300 lg:mt-8"
            >
              Auraui is your gateway to mastering modern web development, with
              resources that adapt to your pace and level.
            </motion.p>
            <motion.div variants={itemVariants} className="mt-10">
              <motion.div
                variants={buttonVariants}
                initial="rest"
                whileHover="hover"
                whileTap={{ scale: 0.95 }}
              >
                <Link
                  href="#"
                  className="inline-flex items-center justify-center px-6 py-3 font-sans text-base font-semibold transition-all duration-200 rounded-full bg-gradient-to-r from-blue-500 to-purple-600 text-white hover:from-blue-600 hover:to-purple-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 focus:ring-offset-gray-900"
                  role="button"
                >
                  Learn More
                  <motion.svg
                    xmlns="http://www.w3.org/2000/svg"
                    className="h-5 w-5 ml-2"
                    viewBox="0 0 20 20"
                    fill="currentColor"
                    animate={{ x: [0, 5, 0] }}
                    transition={{ repeat: Infinity, duration: 1.5 }}
                  >
                    <path
                      fillRule="evenodd"
                      d="M10.293 3.293a1 1 0 011.414 0l6 6a1 1 0 010 1.414l-6 6a1 1 0 01-1.414-1.414L14.586 11H3a1 1 0 110-2h11.586l-4.293-4.293a1 1 0 010-1.414z"
                      clipRule="evenodd"
                    />
                  </motion.svg>
                </Link>
              </motion.div>
            </motion.div>
          </motion.div>

          <motion.div variants={itemVariants}>
            <ul className="space-y-5">
              {features.map((feature, index) => (
                <motion.li
                  key={index}
                  variants={itemVariants}
                  whileHover={{ x: 10 }}
                  className="transform transition-all duration-200 hover:shadow-lg hover:bg-gray-800/30 p-4 rounded-xl"
                >
                  <h3 className="font-sans text-2xl font-semibold text-white">
                    {feature.title}
                  </h3>
                  <p className="mt-3 font-sans text-lg font-normal leading-8 text-gray-300">
                    {feature.description}
                  </p>
                </motion.li>
              ))}
            </ul>
          </motion.div>
        </div>

        <div className="mt-12 flex justify-start md:justify-center mb-16">
          <VideoDialog videoSrc="https://www.youtube.com/embed/5Peo-ivmupE?si=YNTQs95Rx0-4FOvp" />
        </div>
      </motion.div>
    </section>
  );
};

export default FeatureTwo;

"use client";

import { useState } from "react";
import { AnimatePresence, motion } from "framer-motion";
import { Play, XIcon } from "lucide-react";
import { cn } from "@/registry/default/lib/utils";

type AnimationStyle =
  | "from-bottom"
  | "from-center"
  | "from-top"
  | "from-left"
  | "from-right"
  | "fade"
  | "top-in-bottom-out"
  | "left-in-right-out";

interface HeroVideoProps {
  animationStyle?: AnimationStyle;
  videoSrc: string;
  className?: string;
}

const animationVariants = {
  "from-bottom": {
    initial: { y: "100%", opacity: 0 },
    animate: { y: 0, opacity: 1 },
    exit: { y: "100%", opacity: 0 },
  },
  "from-center": {
    initial: { scale: 0.5, opacity: 0 },
    animate: { scale: 1, opacity: 1 },
    exit: { scale: 0.5, opacity: 0 },
  },
  "from-top": {
    initial: { y: "-100%", opacity: 0 },
    animate: { y: 0, opacity: 1 },
    exit: { y: "-100%", opacity: 0 },
  },
  "from-left": {
    initial: { x: "-100%", opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: "-100%", opacity: 0 },
  },
  "from-right": {
    initial: { x: "100%", opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: "100%", opacity: 0 },
  },
  fade: {
    initial: { opacity: 0 },
    animate: { opacity: 1 },
    exit: { opacity: 0 },
  },
  "top-in-bottom-out": {
    initial: { y: "-100%", opacity: 0 },
    animate: { y: 0, opacity: 1 },
    exit: { y: "100%", opacity: 0 },
  },
  "left-in-right-out": {
    initial: { x: "-100%", opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: "100%", opacity: 0 },
  },
};

/**
 *
 * This Components refer from magicui
 *
 * Source: [https://magicui.design/docs/components/hero-video-dialog]
 *
 */
export function VideoDialog({
  animationStyle = "from-center",
  videoSrc,
  className,
}: HeroVideoProps) {
  const [isVideoOpen, setIsVideoOpen] = useState(false);
  const selectedAnimation = animationVariants[animationStyle];

  return (
    <div className={cn("relative", className)}>
      <div
        className="relative cursor-pointer group"
        onClick={() => setIsVideoOpen(true)}
      >
        <div className="absolute inset-0 scale-100 transition-all duration-300 ease-in-out rounded-2xl">
          <div className="bg-primary/5 hover:bg-primary/10 flex items-center justify-center rounded-full backdrop-blur-lg size-28 transition-colors duration-300">
            <div className="flex items-center justify-center bg-gradient-to-br from-primary via-primary/90 to-primary/80 shadow-lg hover:shadow-primary/20 rounded-full size-20 transition-all ease-in-out duration-300 relative group-hover:scale-110 scale-100 group-active:scale-95">
              <Play
                className="size-8 dark:text-black dark:fill-black text-white fill-white group-hover:scale-110 scale-100 transition-all duration-300 ease-in-out"
                style={{
                  filter:
                    "drop-shadow(0 8px 6px rgb(0 0 0 / 0.1)) drop-shadow(0 4px 4px rgb(0 0 0 / 0.08))",
                }}
              />
            </div>
          </div>
        </div>
      </div>
      <AnimatePresence>
        {isVideoOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            onClick={() => setIsVideoOpen(false)}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-md"
          >
            <motion.div
              {...selectedAnimation}
              transition={{ type: "spring", damping: 30, stiffness: 300 }}
              className="relative w-full max-w-4xl aspect-video mx-4 md:mx-0"
            >
              <motion.button className="absolute -top-12 right-0 text-white text-xl bg-neutral-900/50 ring-1 backdrop-blur-md rounded-full p-2 dark:bg-neutral-100/50 dark:text-black">
                <XIcon className="size-5" />
              </motion.button>
              <div className="size-full border-2 border-white rounded-2xl overflow-hidden isolate z-[1] relative">
                <iframe
                  src={videoSrc}
                  className="size-full rounded-2xl"
                  allowFullScreen
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                ></iframe>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
