import FeatureThree from "@/registry/default/blocks/feature-03/components/feature-three";

export default function Page() {
  return <FeatureThree />;
}

"use client";

import { VideoDialog } from "@/registry/default/blocks/feature-03/components/video-dialog";
import { Card, CardContent } from "@/components/ui/card";

const features = [
  {
    title: "Pre-designed Blocks",
    description:
      "30+ pre-designed components to accelerate your development workflow.",
  },
  {
    title: "Customizable Variants",
    description: "Easily customize components with variants and Tailwind CSS.",
  },
  {
    title: "Built on ShadCN UI",
    description:
      "Leverage the power of ShadCN UI for modern, responsive components.",
  },
  {
    title: "Rapid Development",
    description:
      "Accelerate development with optimized components and responsive design.",
  },
];

export default function FeatureThree() {
  return (
    <section className="py-16 md:py-24 lg:py-28 bg-gradient-to-b from-background to-background/80">
      <div className="container px-4 md:px-6">
        <div className="grid lg:gap-10 gap-32 lg:grid-cols-2 ">
          <div className="space-y-8">
            <h2 className="text-3xl font-bold tracking-tighter sm:text-4xl md:text-5xl">
              Elevate your UI
              <span className="block text-primary mt-1">design process</span>
              <span className="block text-2xl sm:text-3xl md:text-4xl mt-2 text-muted-foreground">
                with our versatile component library.
              </span>
            </h2>
            <p className="max-w-[600px] text-muted-foreground md:text-lg/relaxed lg:text-base/relaxed xl:text-lg/relaxed">
              Streamline your development workflow with our comprehensive
              library of pre-designed, customizable components. Built on ShadCN
              UI, our toolkit ensures modern, responsive, and highly performant
              designs.
            </p>
            <div className="mx-auto">
              <VideoDialog videoSrc="https://www.youtube.com/embed/5Peo-ivmupE?si=YNTQs95Rx0-4FOvp" />
            </div>
          </div>
          <div className="grid gap-6 sm:grid-cols-2">
            {features.map((feature, index) => (
              <Card
                key={index}
                className="transition-all duration-200 hover:shadow-lg"
              >
                <CardContent className="p-6 space-y-2">
                  <h3 className="text-xl font-semibold">{feature.title}</h3>
                  <p className="text-sm text-muted-foreground">
                    {feature.description}
                  </p>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
}

"use client";

import { useState } from "react";
import { AnimatePresence, motion } from "framer-motion";
import { Play, XIcon } from "lucide-react";
import { cn } from "@/lib/utils";

type AnimationStyle =
  | "from-bottom"
  | "from-center"
  | "from-top"
  | "from-left"
  | "from-right"
  | "fade"
  | "top-in-bottom-out"
  | "left-in-right-out";

interface HeroVideoProps {
  animationStyle?: AnimationStyle;
  videoSrc: string;
  className?: string;
}

const animationVariants = {
  "from-bottom": {
    initial: { y: "100%", opacity: 0 },
    animate: { y: 0, opacity: 1 },
    exit: { y: "100%", opacity: 0 },
  },
  "from-center": {
    initial: { scale: 0.5, opacity: 0 },
    animate: { scale: 1, opacity: 1 },
    exit: { scale: 0.5, opacity: 0 },
  },
  "from-top": {
    initial: { y: "-100%", opacity: 0 },
    animate: { y: 0, opacity: 1 },
    exit: { y: "-100%", opacity: 0 },
  },
  "from-left": {
    initial: { x: "-100%", opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: "-100%", opacity: 0 },
  },
  "from-right": {
    initial: { x: "100%", opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: "100%", opacity: 0 },
  },
  fade: {
    initial: { opacity: 0 },
    animate: { opacity: 1 },
    exit: { opacity: 0 },
  },
  "top-in-bottom-out": {
    initial: { y: "-100%", opacity: 0 },
    animate: { y: 0, opacity: 1 },
    exit: { y: "100%", opacity: 0 },
  },
  "left-in-right-out": {
    initial: { x: "-100%", opacity: 0 },
    animate: { x: 0, opacity: 1 },
    exit: { x: "100%", opacity: 0 },
  },
};

/**
 *
 * This Components refer from magicui
 *
 * Source: [https://magicui.design/docs/components/hero-video-dialog]
 *
 */
export function VideoDialog({
  animationStyle = "from-center",
  videoSrc,
  className,
}: HeroVideoProps) {
  const [isVideoOpen, setIsVideoOpen] = useState(false);
  const selectedAnimation = animationVariants[animationStyle];

  return (
    <div className={cn("relative", className)}>
      <div
        className="relative cursor-pointer group"
        onClick={() => setIsVideoOpen(true)}
      >
        <div className="absolute inset-0 scale-100 transition-all duration-300 ease-in-out rounded-2xl">
          <div className="bg-primary/5 hover:bg-primary/10 flex items-center justify-center rounded-full backdrop-blur-lg size-20 md:size-28 transition-colors duration-300">
            <div className="flex items-center justify-center bg-gradient-to-br from-primary via-primary/90 to-primary/80 shadow-lg hover:shadow-primary/20 rounded-full size-20 transition-all ease-in-out duration-300 relative group-hover:scale-110 scale-100 group-active:scale-95">
              <Play
                className="size-8 dark:text-black dark:fill-black text-white fill-white group-hover:scale-110 scale-100 transition-all duration-300 ease-in-out"
                style={{
                  filter:
                    "drop-shadow(0 8px 6px rgb(0 0 0 / 0.1)) drop-shadow(0 4px 4px rgb(0 0 0 / 0.08))",
                }}
              />
            </div>
          </div>
        </div>
      </div>
      <AnimatePresence>
        {isVideoOpen && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            onClick={() => setIsVideoOpen(false)}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-md"
          >
            <motion.div
              {...selectedAnimation}
              transition={{ type: "spring", damping: 30, stiffness: 300 }}
              className="relative w-full max-w-4xl aspect-video mx-4 md:mx-0"
            >
              <motion.button className="absolute -top-12 right-0 text-white text-xl bg-neutral-900/50 ring-1 backdrop-blur-md rounded-full p-2 dark:bg-neutral-100/50 dark:text-black">
                <XIcon className="size-5" />
              </motion.button>
              <div className="size-full border-2 border-white rounded-2xl overflow-hidden isolate z-[1] relative">
                <iframe
                  src={videoSrc}
                  className="size-full rounded-2xl"
                  allowFullScreen
                  allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share"
                ></iframe>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}
