import LogoTwo from "@/registry/default/blocks/logo-02/components/logo-two";

export default function Page() {
  return (
    <div className="container flex justify-center items-center py-2">
      <LogoTwo />
    </div>
  );
}

import React from "react";
import { Button } from "@/components/ui/button";
import { ChevronRight } from "lucide-react";
import Marquee from "./marquee";

const LogoTwo = () => {
  return (
    <section className="py-16 rounded-xl bg-gradient-to-b from-gray-900 via-gray-800 to-black">
      <div className="container mx-auto px-6 lg:px-12">
        <div className="flex flex-col items-center text-center lg:text-left lg:items-start lg:flex-row">
          <div className="lg:w-1/3">
            <h2 className="text-white text-4xl font-semibold leading-tight">
              Trusted by companies <br />
              <span className="p-2 italic text-4xl font-sans font-bold bg-gradient-to-r from-pink-500 via-pink-400 to-pink-300 bg-clip-text text-transparent">
                all over the world
              </span>
            </h2>
            <Button
              variant="outline"
              className="mt-8 flex items-center space-x-2"
            >
              <span>Learn More</span>
              <ChevronRight className="h-5 w-5" />
            </Button>
          </div>

          <div className="lg:w-2/3 mt-12 lg:mt-0">
            <div className="grid gap-6">
              <Marquee
                pauseOnHover
                className="border rounded-lg shadow-lg bg-gray-50 p-4"
              >
                {[1, 2, 3, 4, 5].map((num, index) => (
                  <div
                    key={index}
                    className="flex items-center justify-center gap-10 [&_svg]:mr-10"
                  >
                    <img
                      src={`/logo/logo-${num}.svg`}
                      alt={`Logo ${index + 1}`}
                      className="h-12 w-auto opacity-80 hover:opacity-100 transition duration-200"
                    />
                  </div>
                ))}
              </Marquee>
              <Marquee
                pauseOnHover
                reverse
                speed={20}
                className="border rounded-lg shadow-lg bg-gray-50 p-4"
              >
                {[6, 7, 8, 9, 10].map((num, index) => (
                  <div
                    key={index}
                    className="flex items-center justify-center gap-10"
                  >
                    <img
                      src={`/logo/logo-${num}.svg`}
                      alt={`Logo ${index + 1}`}
                      className="h-12 w-auto opacity-80 hover:opacity-100 transition duration-200"
                    />
                  </div>
                ))}
              </Marquee>
              <Marquee
                pauseOnHover
                speed={10}
                className="border rounded-lg shadow-lg bg-gray-50 p-4"
              >
                {[11, 12, 13, 14].map((num, index) => (
                  <div key={index} className="flex items-center justify-center">
                    <img
                      src={`/logo/logo-${num}.svg`}
                      alt={`Logo ${index + 1}`}
                      className="h-12 w-auto opacity-80 hover:opacity-100 transition duration-200"
                    />
                  </div>
                ))}
              </Marquee>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default LogoTwo;

import { cn } from "@/registry/default/lib/utils";
import { ComponentProps, CSSProperties } from "react";

interface MarqueeProps extends ComponentProps<"div"> {
  className?: string;
  reverse?: boolean;
  pauseOnHover?: boolean;
  children?: React.ReactNode;
  vertical?: boolean;
  repeat?: number;
  speed?: number;
  gap?: string;
}

export default function Marquee({
  className,
  reverse,
  pauseOnHover = false,
  children,
  vertical = false,
  repeat = 4,
  speed = 40,
  gap = "1rem",
  ...props
}: MarqueeProps) {
  return (
    <div
      {...props}
      className={cn(
        "group flex overflow-hidden p-2",
        {
          "flex-row": !vertical,
          "flex-col": vertical,
        },
        className,
      )}
      style={
        {
          "--duration": `${speed}s`,
          "--gap": gap,
        } as React.CSSProperties
      }
    >
      {Array(repeat)
        .fill(0)
        .map((_, i) => (
          <div
            key={i}
            className={cn("flex shrink-0 justify-around", {
              "animate-marquee flex-row": !vertical,
              "animate-marquee-vertical flex-col": vertical,
              "group-hover:[animation-play-state:paused]": pauseOnHover,
              "[animation-direction:reverse]": reverse,
            })}
            style={{ gap }}
          >
            {children}
          </div>
        ))}
    </div>
  );
}

module.exports = {
  theme: {
    extend: {
      animation: {
        marquee: "marquee var(--duration) linear infinite",
        "marquee-vertical": "marquee-vertical var(--duration) linear infinite",
      },
      keyframes: {
        marquee: {
          from: { transform: "translateX(0)" },
          to: { transform: "translateX(calc(-100% - var(--gap)))" },
        },
        "marquee-vertical": {
          from: { transform: "translateY(0)" },
          to: { transform: "translateY(calc(-100% - var(--gap)))" },
        },
      },
    },
  },
};
