import TestimonialTwo from "@/registry/default/blocks/testimonial-02/components/testimonial-two";

export default function Page() {
  return (
    <div className="container mx-auto ">
      <div className="min-h-screen w-full flex items-center justify-center">
        <TestimonialTwo />
      </div>
    </div>
  );
}

"use client";

import Autoplay from "embla-carousel-autoplay";
import {
  Carousel,
  CarouselContent,
  CarouselItem,
} from "@/registry/default/ui/carousel";
import { TestimonialCard } from "./testimonial-card";

interface Testimonial {
  image: string;
  quote: string;
  author: string;
  role: string;
}

const testimonials: Testimonial[] = [
  {
    quote:
      '"ShapeX-UI has drastically improved our development speed. The ready-made components save us hours of work."',
    author: "Emma Watson",
    role: "Lead Frontend Engineer",
    image:
      "https://images.unsplash.com/photo-1544725176-7c40e5a71c5e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=3534&q=80",
  },
  {
    quote:
      '"We were able to achieve a cohesive design system for our platform in no time. ShapeX-UI is an essential tool for our team."',
    author: "Liam Brown",
    role: "Design Manager",
    image:
      "https://images.unsplash.com/photo-1535713875002-d1d0cf377fde?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8YXZhdGFyfGVufDB8fDB8fHww&auto=format&fit=crop&w=800&q=60",
  },
  {
    quote:
      '"Our clients are always impressed with the smooth animations and clean designs. ShapeX-UI gives us a competitive edge."',
    author: "Sophia Davis",
    role: "Creative Director",
    image:
      "https://images.unsplash.com/photo-1580489944761-15a19d654956?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8NXx8YXZhdGFyfGVufDB8fDB8fHww&auto=format&fit=crop&w=800&q=60",
  },
  {
    quote:
      '"As a freelance designer, ShapeX-UI has become my go-to library. It’s versatile and saves me so much time."',
    author: "Noah Wilson",
    role: "Freelance UX Designer",
    image:
      "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8MTB8fGF2YXRhcnxlbnwwfHwwfHx8MA%3D%3D&auto=format&fit=crop&w=800&q=60",
  },
  {
    quote:
      '"Using ShapeX-UI, we’ve managed to reduce the time-to-market for our applications significantly."',
    author: "Isabella Martinez",
    role: "Product Owner",
    image:
      "https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=3540&q=80",
  },
  {
    quote:
      '"ShapeX-UIs components are not only functional but also aesthetically pleasing. Our users love the designs!"',
    author: "James Anderson",
    role: "UI/UX Specialist",
    image:
      "https://images.unsplash.com/photo-1599566150163-29194dcaad36?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=3387&q=80",
  },
];

const TestimonialTwo = () => {
  return (
    <Carousel
      className="relative w-full"
      opts={{
        align: "start",
        loop: true,
      }}
      plugins={[
        Autoplay({
          delay: 5000,
        }),
      ]}
    >
      <CarouselContent>
        {testimonials.map((testimonial, index) => (
          <CarouselItem key={index} className="xl:basis-1/2">
            <TestimonialCard {...testimonial} />
          </CarouselItem>
        ))}
      </CarouselContent>
    </Carousel>
  );
};

export default TestimonialTwo;

import { Star } from "lucide-react";

interface TestimonialCardProps {
  image: string;
  quote: string;
  author: string;
  role: string;
}

export function TestimonialCard({
  image,
  quote,
  author,
  role,
}: TestimonialCardProps) {
  return (
    <div className="relative h-[300px] sm:h-[350px] md:h-[500px] w-full overflow-hidden bg-[#f5f0eb] rounded-lg shadow-md">
      <div className="absolute md:max-w-lg lg:max-w-full inset-0 p-4 sm:p-6 md:p-8 flex flex-col justify-between">
        <div>
          <h2 className="text-3xl sm:text-4xl md:text-5xl font-serif text-gray-800 mb-6 md:mb-16">
            Testimonial
          </h2>

          <div className="relative z-10 bg-white p-4 md:max-w-xl xl:max-w-sm sm:p-6 rounded-sm shadow-sm">
            <div className="flex gap-1 mb-3">
              {[...Array(5)].map((_, i) => (
                <Star
                  key={i}
                  className="w-4 h-4 sm:w-5 sm:h-5 fill-yellow-400 text-yellow-400"
                />
              ))}
            </div>
            <p className="text-sm sm:text-base text-gray-700 mb-4">{quote}</p>
            <div className="text-gray-600 flex items-center justify-between">
              <div className="order-last">
                <p className="font-medium text-sm sm:text-base">{author}</p>
                <p className="text-xs sm:text-sm">{role}</p>
              </div>
              <img
                src={image}
                alt={`${author} avatar`}
                className="w-12 order-1 h-12 sm:w-16 sm:h-16 md:hidden block object-cover rounded-full"
              />
            </div>
          </div>
        </div>
      </div>

      <div
        className="absolute top-0 right-0 w-[55%] h-full hidden md:block"
        style={{
          clipPath:
            "polygon(68% 0, 100% 0, 100% 49%, 100% 100%, 66% 100%, 16% 51%, 16% 52%)",
        }}
      >
        <img
          src={image}
          alt={`${author} background`}
          className="w-full h-full object-cover"
        />
      </div>
    </div>
  );
}
